import { Megaphone, Sparkles, HelpCircle, Bell } from 'lucide-react';

export type TemplateType = 'promotional' | 'inspirational' | 'question' | 'announcement';

interface Template {
  id: TemplateType;
  name: string;
  icon: React.ReactNode;
  description: string;
  template: string;
}

const templates: Template[] = [
  {
    id: 'promotional',
    name: 'Promotional',
    icon: <Megaphone className="w-5 h-5" />,
    description: 'Promote products or services',
    template: "ðŸš€ Exciting news! [Your product/service] is here to [benefit]. Don't miss out on [offer]!\n\nLearn more: [link]"
  },
  {
    id: 'inspirational',
    name: 'Inspirational',
    icon: <Sparkles className="w-5 h-5" />,
    description: 'Motivate your audience',
    template: "âœ¨ [Inspirational quote or message]\n\nRemember: [Supporting thought]\n\nWhat's your take on this?"
  },
  {
    id: 'question',
    name: 'Question',
    icon: <HelpCircle className="w-5 h-5" />,
    description: 'Engage with questions',
    template: "ðŸ¤” Quick question for you:\n\n[Your question here]\n\nDrop your answer below! ðŸ‘‡"
  },
  {
    id: 'announcement',
    name: 'Announcement',
    icon: <Bell className="w-5 h-5" />,
    description: 'Share important updates',
    template: "ðŸ“¢ Big announcement!\n\n[Your news here]\n\nStay tuned for more updates!"
  }
];

interface TemplateSelectorProps {
  selected: TemplateType;
  onSelect: (template: TemplateType, content: string) => void;
}

export function TemplateSelector({ selected, onSelect }: TemplateSelectorProps) {
  return (
    <div className="space-y-3">
      <h3 className="text-sm font-medium text-foreground">Choose Template</h3>
      <div className="grid grid-cols-2 gap-3">
        {templates.map((template) => (
          <button
            key={template.id}
            onClick={() => onSelect(template.id, template.template)}
            className={`group relative p-4 rounded-lg border text-left transition-all duration-200 ${
              selected === template.id
                ? 'border-primary bg-primary/5 shadow-soft'
                : 'border-border bg-card hover:border-primary/50 hover:bg-secondary/50'
            }`}
          >
            <div className={`inline-flex p-2 rounded-md mb-2 transition-colors ${
              selected === template.id
                ? 'gradient-primary text-primary-foreground'
                : 'bg-secondary text-muted-foreground group-hover:text-primary'
            }`}>
              {template.icon}
            </div>
            <h4 className="font-medium text-card-foreground">{template.name}</h4>
            <p className="text-xs text-muted-foreground mt-1">{template.description}</p>
          </button>
        ))}
      </div>
    </div>
  );
}

import { TrendingUp, Plus } from 'lucide-react';

const trendingHashtags = [
  { tag: '#marketing', uses: '2.1M' },
  { tag: '#socialmedia', uses: '1.8M' },
  { tag: '#business', uses: '3.2M' },
  { tag: '#entrepreneur', uses: '1.5M' },
  { tag: '#motivation', uses: '2.7M' },
  { tag: '#success', uses: '1.9M' },
  { tag: '#growth', uses: '890K' },
  { tag: '#tips', uses: '1.2M' },
  { tag: '#viral', uses: '4.1M' },
  { tag: '#trending', uses: '3.8M' },
];

interface HashtagSuggestionsProps {
  onAdd: (hashtag: string) => void;
  usedHashtags: string[];
}

export function HashtagSuggestions({ onAdd, usedHashtags }: HashtagSuggestionsProps) {
  return (
    <div className="space-y-3">
      <div className="flex items-center gap-2">
        <TrendingUp className="w-4 h-4 text-accent" />
        <h3 className="text-sm font-medium text-foreground">Trending Hashtags</h3>
      </div>
      <div className="flex flex-wrap gap-2">
        {trendingHashtags.map((item) => {
          const isUsed = usedHashtags.includes(item.tag);
          return (
            <button
              key={item.tag}
              onClick={() => !isUsed && onAdd(item.tag)}
              disabled={isUsed}
              className={`group inline-flex items-center gap-1.5 px-3 py-1.5 rounded-full text-sm transition-all duration-200 ${
                isUsed
                  ? 'bg-primary/10 text-primary cursor-default'
                  : 'bg-secondary text-secondary-foreground hover:bg-primary hover:text-primary-foreground'
              }`}
            >
              <span className="font-medium">{item.tag}</span>
              <span className={`text-xs ${isUsed ? 'text-primary/60' : 'text-muted-foreground group-hover:text-primary-foreground/70'}`}>
                {item.uses}
              </span>
              {!isUsed && (
                <Plus className="w-3 h-3 opacity-0 group-hover:opacity-100 transition-opacity" />
              )}
            </button>
          );
        })}
      </div>
    </div>
  );
}

import { useState, useEffect } from 'react';
import { AlertCircle } from 'lucide-react';

interface PostEditorProps {
  value: string;
  onChange: (value: string) => void;
  maxLength?: number;
}

export function PostEditor({ value, onChange, maxLength = 280 }: PostEditorProps) {
  const [charCount, setCharCount] = useState(value.length);
  const remaining = maxLength - charCount;
  const isOverLimit = remaining < 0;
  const isWarning = remaining <= 20 && remaining > 0;

  useEffect(() => {
    setCharCount(value.length);
  }, [value]);

  return (
    <div className="space-y-2">
      <div className="flex items-center justify-between">
        <h3 className="text-sm font-medium text-foreground">Your Post</h3>
        <div className={`text-sm font-medium flex items-center gap-1 ${
          isOverLimit ? 'text-destructive' : isWarning ? 'text-accent' : 'text-muted-foreground'
        }`}>
          {isOverLimit && <AlertCircle className="w-4 h-4" />}
          <span>{remaining}</span>
        </div>
      </div>
      <textarea
        value={value}
        onChange={(e) => onChange(e.target.value)}
        placeholder="Write your engaging post here..."
        className={`w-full h-40 p-4 rounded-lg border bg-card text-card-foreground placeholder:text-muted-foreground resize-none transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-background ${
          isOverLimit
            ? 'border-destructive focus:ring-destructive'
            : 'border-input focus:ring-primary'
        }`}
      />
      <div className="h-1 w-full bg-secondary rounded-full overflow-hidden">
        <div
          className={`h-full transition-all duration-300 ${
            isOverLimit ? 'bg-destructive' : isWarning ? 'bg-accent' : 'gradient-primary'
          }`}
          style={{ width: `${Math.min((charCount / maxLength) * 100, 100)}%` }}
        />
      </div>
    </div>
  );
}

import { User, Heart, MessageCircle, Share2, Bookmark } from 'lucide-react';

interface PostPreviewProps {
  content: string;
}

export function PostPreview({ content }: PostPreviewProps) {
  const formatContent = (text: string) => {
    return text.split(/(\s+)/).map((word, i) => {
      if (word.startsWith('#')) {
        return <span key={i} className="text-primary font-medium">{word}</span>;
      }
      if (word.startsWith('@')) {
        return <span key={i} className="text-primary font-medium">{word}</span>;
      }
      if (word.match(/^https?:\/\//)) {
        return <span key={i} className="text-primary underline">{word}</span>;
      }
      return word;
    });
  };

  return (
    <div className="space-y-3">
      <h3 className="text-sm font-medium text-foreground">Preview</h3>
      <div className="bg-card border border-border rounded-xl p-4 shadow-card animate-scale-in">
        <div className="flex items-start gap-3">
          <div className="w-10 h-10 rounded-full gradient-primary flex items-center justify-center flex-shrink-0">
            <User className="w-5 h-5 text-primary-foreground" />
          </div>
          <div className="flex-1 min-w-0">
            <div className="flex items-center gap-2">
              <span className="font-semibold text-card-foreground">Your Brand</span>
              <span className="text-muted-foreground text-sm">@yourbrand</span>
              <span className="text-muted-foreground text-sm">Â· now</span>
            </div>
            <div className="mt-2 text-card-foreground whitespace-pre-wrap break-words">
              {content ? formatContent(content) : (
                <span className="text-muted-foreground italic">Your post will appear here...</span>
              )}
            </div>
            <div className="flex items-center justify-between mt-4 pt-3 border-t border-border">
              <button className="flex items-center gap-2 text-muted-foreground hover:text-primary transition-colors">
                <MessageCircle className="w-4 h-4" />
                <span className="text-sm">24</span>
              </button>
              <button className="flex items-center gap-2 text-muted-foreground hover:text-accent transition-colors">
                <Heart className="w-4 h-4" />
                <span className="text-sm">182</span>
              </button>
              <button className="flex items-center gap-2 text-muted-foreground hover:text-primary transition-colors">
                <Share2 className="w-4 h-4" />
                <span className="text-sm">12</span>
              </button>
              <button className="text-muted-foreground hover:text-primary transition-colors">
                <Bookmark className="w-4 h-4" />
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

import { useState } from 'react';
import { Copy, Check, Wand2 } from 'lucide-react';
import { TemplateSelector, TemplateType } from './components/TemplateSelector';
import { HashtagSuggestions } from './components/HashtagSuggestions';
import { PostEditor } from './components/PostEditor';
import { PostPreview } from './components/PostPreview';

function App() {
  const [selectedTemplate, setSelectedTemplate] = useState<TemplateType>('promotional');
  const [postContent, setPostContent] = useState('');
  const [copied, setCopied] = useState(false);

  const handleTemplateSelect = (template: TemplateType, content: string) => {
    setSelectedTemplate(template);
    setPostContent(content);
  };

  const handleAddHashtag = (hashtag: string) => {
    setPostContent(prev => prev + (prev.endsWith(' ') || prev === '' ? '' : ' ') + hashtag);
  };

  const handleCopy = async () => {
    await navigator.clipboard.writeText(postContent);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  const extractHashtags = (text: string): string[] => {
    const matches = text.match(/#\w+/g);
    return matches || [];
  };

  return (
    <div className="min-h-screen py-8 px-4">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="text-center mb-10 animate-fade-in">
          <div className="inline-flex items-center gap-2 px-4 py-2 rounded-full bg-primary/10 text-primary mb-4">
            <Wand2 className="w-4 h-4" />
            <span className="text-sm font-medium">AI-Powered</span>
          </div>
          <h1 className="text-4xl md:text-5xl font-bold text-foreground mb-3">
            Social Post <span className="gradient-primary bg-clip-text text-transparent">Generator</span>
          </h1>
          <p className="text-muted-foreground text-lg max-w-xl mx-auto">
            Create engaging social media posts with customizable templates and trending hashtags
          </p>
        </div>

        {/* Main Content */}
        <div className="grid lg:grid-cols-2 gap-6">
          {/* Left Column - Editor */}
          <div className="space-y-6 animate-fade-in" style={{ animationDelay: '0.1s' }}>
            <div className="bg-card border border-border rounded-2xl p-6 shadow-card">
              <TemplateSelector
                selected={selectedTemplate}
                onSelect={handleTemplateSelect}
              />
            </div>

            <div className="bg-card border border-border rounded-2xl p-6 shadow-card">
              <PostEditor
                value={postContent}
                onChange={setPostContent}
              />
            </div>

            <div className="bg-card border border-border rounded-2xl p-6 shadow-card">
              <HashtagSuggestions
                onAdd={handleAddHashtag}
                usedHashtags={extractHashtags(postContent)}
              />
            </div>
          </div>

          {/* Right Column - Preview */}
          <div className="space-y-6 animate-fade-in" style={{ animationDelay: '0.2s' }}>
            <div className="bg-card border border-border rounded-2xl p-6 shadow-card lg:sticky lg:top-8">
              <PostPreview content={postContent} />

              <button
                onClick={handleCopy}
                disabled={!postContent}
                className={`w-full mt-6 py-3 px-4 rounded-lg font-medium flex items-center justify-center gap-2 transition-all duration-200 ${
                  postContent
                    ? 'gradient-primary text-primary-foreground hover:shadow-glow'
                    : 'bg-muted text-muted-foreground cursor-not-allowed'
                }`}
              >
                {copied ? (
                  <>
                    <Check className="w-5 h-5" />
                    Copied!
                  </>
                ) : (
                  <>
                    <Copy className="w-5 h-5" />
                    Copy to Clipboard
                  </>
                )}
              </button>

              {/* Tips Section */}
              <div className="mt-6 p-4 rounded-lg bg-secondary/50 border border-border">
                <h4 className="text-sm font-medium text-foreground mb-2">Quick Tips</h4>
                <ul className="text-sm text-muted-foreground space-y-1">
                  <li>â€¢ Keep posts concise and engaging</li>
                  <li>â€¢ Use 3-5 relevant hashtags</li>
                  <li>â€¢ Include a clear call-to-action</li>
                  <li>â€¢ Add emojis for visual appeal</li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;

