@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 250 20% 98%;
    --foreground: 250 30% 10%;
    --card: 0 0% 100%;
    --card-foreground: 250 30% 10%;
    --popover: 0 0% 100%;
    --popover-foreground: 250 30% 10%;
    --primary: 262 83% 58%;
    --primary-foreground: 0 0% 100%;
    --secondary: 250 20% 96%;
    --secondary-foreground: 250 30% 20%;
    --muted: 250 15% 92%;
    --muted-foreground: 250 15% 45%;
    --accent: 330 85% 60%;
    --accent-foreground: 0 0% 100%;
    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 100%;
    --border: 250 20% 90%;
    --input: 250 20% 90%;
    --ring: 262 83% 58%;
    --radius: 0.75rem;

    --gradient-primary: linear-gradient(135deg, hsl(262 83% 58%), hsl(220 90% 56%));
    --gradient-accent: linear-gradient(135deg, hsl(330 85% 60%), hsl(262 83% 58%));
    --gradient-bg: linear-gradient(135deg, hsl(262 60% 96%), hsl(220 60% 96%));
    --shadow-soft: 0 4px 20px -4px hsl(262 83% 58% / 0.15);
    --shadow-glow: 0 0 30px hsl(262 83% 58% / 0.25);
    --shadow-card: 0 8px 32px -8px hsl(250 30% 20% / 0.1);
  }

  .dark {
    --background: 250 30% 8%;
    --foreground: 250 10% 95%;
    --card: 250 25% 12%;
    --card-foreground: 250 10% 95%;
    --popover: 250 25% 12%;
    --popover-foreground: 250 10% 95%;
    --primary: 262 83% 65%;
    --primary-foreground: 0 0% 100%;
    --secondary: 250 20% 16%;
    --secondary-foreground: 250 10% 85%;
    --muted: 250 20% 18%;
    --muted-foreground: 250 15% 60%;
    --accent: 330 85% 65%;
    --accent-foreground: 0 0% 100%;
    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 100%;
    --border: 250 20% 20%;
    --input: 250 20% 20%;
    --ring: 262 83% 65%;

    --gradient-primary: linear-gradient(135deg, hsl(262 83% 65%), hsl(220 90% 60%));
    --gradient-accent: linear-gradient(135deg, hsl(330 85% 65%), hsl(262 83% 65%));
    --gradient-bg: linear-gradient(135deg, hsl(250 30% 8%), hsl(250 35% 6%));
    --shadow-soft: 0 4px 20px -4px hsl(262 83% 65% / 0.2);
    --shadow-glow: 0 0 30px hsl(262 83% 65% / 0.3);
    --shadow-card: 0 8px 32px -8px hsl(0 0% 0% / 0.3);
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground antialiased;
    background: var(--gradient-bg);
    min-height: 100vh;
  }
}

@layer utilities {
  .gradient-primary {
    background: var(--gradient-primary);
  }
  .gradient-accent {
    background: var(--gradient-accent);
  }
  .shadow-soft {
    box-shadow: var(--shadow-soft);
  }
  .shadow-glow {
    box-shadow: var(--shadow-glow);
  }
  .shadow-card {
    box-shadow: var(--shadow-card);
  }
}

